mapper.select:

select

from cs_server
where isDeleted != 1 AND areaId = #{areaId,jdbcType=INTEGER}

		
		select
		
		from cs_server
		where isDeleted != 1 AND serverName like "%"#{serverName,jdbcType=VARCHAR}"%"
		
			
				AND id = #{id,jdbcType=INTEGER} 
			
				

			
				AND areaId = #{areaId,jdbcType=INTEGER} 
			
			
				AND areaName like "%"#{areaName,jdbcType=VARCHAR}"%"  
			
			
				AND openTime = #{openTime,jdbcType=BIGINT} 
			
			
				AND gamedbip like "%"#{gamedbip,jdbcType=VARCHAR}"%"  
			
			
				AND gamedbport = #{gamedbport,jdbcType=INTEGER} 
			
			
				AND socketPort = #{socketPort,jdbcType=INTEGER} 
			
			
				AND logdbip like "%"#{logdbip,jdbcType=VARCHAR}"%"  
			
			
				AND logdbport = #{logdbport,jdbcType=INTEGER} 
			
			
				AND dbPassword like "%"#{dbPassword,jdbcType=VARCHAR}"%"  
			
			
				AND gamedbName like "%"#{gamedbName,jdbcType=VARCHAR}"%"  
			
			
				AND logdbName like "%"#{logdbName,jdbcType=VARCHAR}"%"  
			
			
				AND dbUsername like "%"#{dbUsername,jdbcType=VARCHAR}"%"  
			
			
				AND serverIp like "%"#{serverIp,jdbcType=VARCHAR}"%" 
			
			
				AND serverId = #{serverId,jdbcType=INTEGER}
